import { useEffect, useState } from "react";
import EmployeeService from "../Services/EmployeeService";

const ListEmployee = () => {
    const [employees,setEmployees] = useState([]);
    const editEmployee = (employee)
    useEffect(()=>{
        EmployeeService.getEmployees().then((res) => {
            setEmployees(res.data);
        }).catch(err => console.log(err));
    },[]);
    return(
        <>
        <h2 className="text-center">Employees</h2>
        <div className="row">
            <table className="table table-striped table-bordered">
                <thead>
                    <tr>
                    <th>Employee FirstName</th>
                    <th>Employee LastName</th>
                    <th>Employee Email</th>
                    <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        employees.map(
                            employee => 
                            <tr key= {employee.id}>
                                <td key={employee.firstName}>{employee.firstName}</td>
                                <td key={employee.lastName}>{employee.lastName}</td>
                                <td key={employee.email}>{employee.email}</td>
                                <button className="btn btn-info" onClick={()=>editEmployee(employee.id)}>Update</button>
                            </tr>
                        )
                    }
                </tbody>
            </table>
        </div>
        </>
    );
}
export default ListEmployee;
