import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"
import EmployeeService from "../Services/EmployeeService";

const ViewEmployee = () => {
    const {id} = useParams();
    let [employee, setEmployee] = useState({});
    useEffect(() => {
        EmployeeService.getEmployeeById(id).then( (res) =>{
            setEmployee(employee = res.data);
            console.log(employee)
        }).catch(err => console.log(err));
    },[]);
    return(
    <>
      <div class="card" style={{"width": "18rem","marginTop":"4rem","marginLeft":"auto"}}>
        <img class="card-img-top" src="https://www.svgrepo.com/show/33058/employee.svg" alt="Card image cap"/>
        <div class="card-body">
        <h5 class="card-title">Employee details </h5>
        <ul class="list-group list-group-flush">
        <li class="list-group-item">First Name: {employee.firstName}</li>
        <li class="list-group-item">Last Name: {employee.lastName}</li>
        <li class="list-group-item">Email: {employee.email}</li>
        </ul>
        <button href="#" class="btn btn-primary">Go somewhere</button>
        </div>
     </div>
    </>
    );
}
export default ViewEmployee;
